import React, { useState, useEffect, useCallback } from "react";
import { Match, Pair, Game } from "../lib/database";
import { getMatches, getPairs, getGames } from "../lib/database";
import { MatchResultCalculator } from "./MatchResultCalculator";

interface MatchResultsProps {
  match: Match | null;
  isOpen: boolean;
  onClose: () => void;
  forceRefresh?: number;
}

interface MatchWithPairs extends Match {
  pair1?: Pair;
  pair2?: Pair;
}

const MatchResults: React.FC<MatchResultsProps> = ({
  match,
  isOpen,
  onClose,
  forceRefresh = 0,
}) => {
  const [currentMatch, setCurrentMatch] = useState<MatchWithPairs | null>(null);
  const [games, setGames] = useState<Game[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Funci√≥n para cargar datos frescos del partido
  const loadFreshMatchData = useCallback(
    async (matchId: string) => {
      console.log("=== CARGANDO DATOS FRESCOS DEL PARTIDO ===");
      setLoading(true);
      setError(null);

      try {
        // Cargar partido actualizado
        const matches = await getMatches(match?.tournament_id || "");
        const updatedMatch = matches.find((m) => m.id === matchId);

        if (!updatedMatch) {
          throw new Error("Partido no encontrado");
        }

        // Cargar parejas actualizadas
        const pairs = await getPairs(match?.tournament_id || "");
        const pair1 = pairs.find((p) => p.id === updatedMatch.pair1_id);
        const pair2 = pairs.find((p) => p.id === updatedMatch.pair2_id);

        // Crear match con parejas completas
        const matchWithPairs: MatchWithPairs = {
          ...updatedMatch,
          pair1,
          pair2,
        };

        // Cargar juegos del partido
        const matchGames = await getGames(matchId);

        console.log("Match actualizado:", matchWithPairs);
        console.log("Juegos cargados:", matchGames.length);

        setCurrentMatch(matchWithPairs);
        setGames(matchGames);
      } catch (err) {
        console.error("Error cargando datos del partido:", err);
        setError("Error al cargar los datos del partido");
      } finally {
        setLoading(false);
      }
    },
    [match?.tournament_id]
  );

  // Funci√≥n para obtener el nombre del ganador
  const getWinnerName = (match: MatchWithPairs): string => {
    if (!match.winner_id) return "Empate";

    if (match.winner_id === match.pair1_id && match.pair1) {
      return `${match.pair1.player1?.name} y ${match.pair1.player2?.name}`;
    } else if (match.winner_id === match.pair2_id && match.pair2) {
      return `${match.pair2.player1?.name} y ${match.pair2.player2?.name}`;
    }

    return "Ganador desconocido";
  };

  // Funci√≥n para obtener el texto de resultado usando el calculador
  const getResultDisplayText = (
    match: MatchWithPairs,
    games: Game[]
  ): string => {
    if (!match.winner_id) {
      return "Empate";
    }

    const winnerName = getWinnerName(match);
    return `Ganador: ${winnerName}`;
  };

  // Funci√≥n para obtener el nombre de la pareja
  const getPairName = (pair: Pair | undefined): string => {
    if (!pair) return "Pareja desconocida";
    return `${pair.player1?.name} y ${pair.player2?.name}`;
  };

  // Cargar datos cuando se abre el componente o se fuerza actualizaci√≥n
  useEffect(() => {
    if (isOpen && match) {
      console.log(
        "üîÑ Abriendo componente de resultados para:",
        match.id,
        "forceRefresh:",
        forceRefresh
      );
      loadFreshMatchData(match.id);
    }
  }, [isOpen, match, forceRefresh, loadFreshMatchData]);

  // Funci√≥n para actualizar datos en tiempo real
  const refreshData = async () => {
    if (match) {
      console.log("üîÑ Actualizando datos en tiempo real...");
      await loadFreshMatchData(match.id);
    }
  };

  // Funci√≥n para cerrar el componente
  const handleClose = () => {
    setCurrentMatch(null);
    setGames([]);
    setError(null);
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content results-modal">
        <div className="modal-header">
          <div className="header-icon">üìä</div>
          <h2>Resultados del Partido</h2>
          <button className="close-button" onClick={handleClose}>
            ‚úï
          </button>
        </div>

        <div className="modal-body">
          {loading && (
            <div className="loading-message">
              <div className="spinner"></div>
              <p>Cargando resultados...</p>
            </div>
          )}

          {error && (
            <div className="error-message">
              <p>‚ùå {error}</p>
              <button onClick={refreshData} className="retry-button">
                Reintentar
              </button>
            </div>
          )}

          {!loading && !error && currentMatch && (
            <>
              {/* Informaci√≥n del partido */}
              <div className="match-info">
                <div className="teams">
                  <span className="team1">
                    {getPairName(currentMatch.pair1)}
                  </span>
                  <span className="vs">vs</span>
                  <span className="team2">
                    {getPairName(currentMatch.pair2)}
                  </span>
                </div>

                <div className="match-details">
                  <div className="detail-item">
                    <span className="icon">üèüÔ∏è</span>
                    <span>Cancha {currentMatch.court}</span>
                  </div>
                  <div className="detail-item">
                    <span className="icon">üîÑ</span>
                    <span>Ronda {currentMatch.round}</span>
                  </div>
                </div>

                <div className="winner-info">
                  <span className="icon">üèÜ</span>
                  <span className="winner-text">
                    {getResultDisplayText(currentMatch, games)}
                  </span>
                </div>
              </div>

              {/* Juegos del partido */}
              <div className="games-section">
                <h3>
                  <span className="icon">üéæ</span>
                  Juegos del Partido
                </h3>

                {games.length === 0 ? (
                  <p className="no-games">No hay juegos registrados</p>
                ) : (
                  <div className="games-list">
                    {games.map((game, index) => (
                      <div key={game.id} className="game-item">
                        <div className="game-header">
                          <span className="icon">üéæ</span>
                          <span>Juego {index + 1}</span>
                        </div>
                        <div className="game-score">
                          <span className="score-box">
                            {game.is_tie_break
                              ? game.tie_break_pair1_points
                              : game.pair1_games}
                          </span>
                          <span className="score-separator">-</span>
                          <span className="score-box">
                            {game.is_tie_break
                              ? game.tie_break_pair2_points
                              : game.pair2_games}
                          </span>
                          {game.is_tie_break && (
                            <span className="tie-break-indicator">
                              (Tie Break)
                            </span>
                          )}
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>

              {/* Bot√≥n de actualizaci√≥n */}
              <div className="refresh-section">
                <button onClick={refreshData} className="refresh-button">
                  üîÑ Actualizar Resultados
                </button>
              </div>
            </>
          )}
        </div>

        <div className="modal-footer">
          <button onClick={handleClose} className="back-button">
            <span className="icon">‚Üê</span>
            <span className="back-text">BACK</span>
            <span className="back-label">Volver al Torneo</span>
          </button>
        </div>
      </div>
    </div>
  );
};

export default MatchResults;
