  /**
   * Recalcula todas las estad√≠sticas del torneo
   */
  static async recalculateAllStatistics(tournamentId: string): Promise<{
    success: boolean;
    message: string;
  }> {
    try {
      console.log("üîÑ === INICIO REC√ÅLCULO COMPLETO ===");

      // Obtener todos los datos del torneo
      const pairs = await getPairs(tournamentId);
      const matches = await getMatches(tournamentId);

      // Resetear todas las estad√≠sticas de las parejas
      console.log("üîÑ Reseteando estad√≠sticas de todas las parejas...");
      for (const pair of pairs) {
        await updatePair(pair.id, {
          games_won: 0,
          sets_won: 0,
          points: 0,
          matches_played: 0,
        });
      }

      // Crear un mapa para acumular estad√≠sticas de cada pareja
      const pairStats = new Map<
        string,
        {
          gamesWon: number;
          setsWon: number;
          points: number;
          matchesPlayed: number;
        }
      >();

      // Inicializar estad√≠sticas para todas las parejas
      for (const pair of pairs) {
        pairStats.set(pair.id, {
          gamesWon: 0,
          setsWon: 0,
          points: 0,
          matchesPlayed: 0,
        });
      }

      // Procesar cada partido finalizado y acumular estad√≠sticas
      let processedMatches = 0;
      for (const match of matches) {
        if (match.is_finished) {
          console.log(`üîÑ Procesando partido finalizado: ${match.id}`);
          const games = await getGames(match.id);

          if (games.length > 0) {
            const stats = this.calculateMatchStatistics(match, games);

            // Acumular estad√≠sticas para pareja 1
            const pair1Stats = pairStats.get(match.pair1_id)!;
            pair1Stats.gamesWon += stats.pair1GamesWon;
            pair1Stats.setsWon += stats.pair1SetsWon;
            pair1Stats.points += stats.pair1TotalPoints;
            pair1Stats.matchesPlayed += 1;

            // Acumular estad√≠sticas para pareja 2
            const pair2Stats = pairStats.get(match.pair2_id)!;
            pair2Stats.gamesWon += stats.pair2GamesWon;
            pair2Stats.setsWon += stats.pair2SetsWon;
            pair2Stats.points += stats.pair2TotalPoints;
            pair2Stats.matchesPlayed += 1;

            processedMatches++;
          }
        }
      }

      // Actualizar todas las parejas con sus estad√≠sticas acumuladas
      console.log("üîÑ Actualizando estad√≠sticas acumuladas...");
      for (const pair of pairs) {
        const stats = pairStats.get(pair.id);
        if (stats) {
          await updatePair(pair.id, {
            games_won: stats.gamesWon,
            sets_won: stats.setsWon,
            points: stats.points,
            matches_played: stats.matchesPlayed,
          });
          console.log(
            `üìä Pareja ${pair.id}: ${stats.points} puntos, ${stats.setsWon} sets, ${stats.gamesWon} juegos, ${stats.matchesPlayed} partidos`
          );
        }
      }

      console.log(
        `‚úÖ Estad√≠sticas recalculadas para ${processedMatches} partidos`
      );
      console.log("üîÑ === FIN REC√ÅLCULO COMPLETO ===");

      return {
        success: true,
        message: `‚úÖ Estad√≠sticas recalculadas exitosamente para ${processedMatches} partidos`,
      };
    } catch (error) {
      console.error("‚ùå Error en recalculateAllStatistics:", error);
      return {
        success: false,
        message: "Error al recalcular estad√≠sticas del torneo",
      };
    }
  }

  /**
   * Calcula el ranking de las parejas
   */
  static calculateRanking(pairs: Pair[]): Pair[] {
    return [...pairs].sort((a, b) => {
      // Criterio 1: Puntos totales (descendente) - CRITERIO PRINCIPAL
      if (a.points !== b.points) {
        return b.points - a.points;
      }
      // Criterio 2: Sets ganados (descendente) - CRITERIO DE DESEMPATE
      if (a.sets_won !== b.sets_won) {
        return b.sets_won - a.sets_won;
      }
      // Criterio 3: Juegos ganados (descendente)
      if (a.games_won !== b.games_won) {
        return b.games_won - a.games_won;
      }
      // Criterio 4: Menos partidos jugados (mejor eficiencia)
      return a.matches_played - b.matches_played;
    });
  }
