import React, { useState, useEffect } from "react";
import { Game, Match, Pair } from "../lib/database";
import { createGame, getGames, updateGame, deleteGame } from "../lib/database";
import { SimpleMatchCalculator } from "./SimpleMatchCalculator";

interface GameEditorProps {
  match: Match;
  pairs: Pair[];
  onClose: () => void;
  onUpdate: () => void;
}

const GameEditor: React.FC<GameEditorProps> = ({
  match,
  pairs,
  onClose,
  onUpdate,
}) => {
  const [games, setGames] = useState<Game[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string>("");
  const [editingGames, setEditingGames] = useState<{
    [key: string]: { pair1: number; pair2: number };
  }>({});
  const [editingTieBreaks, setEditingTieBreaks] = useState<{
    [key: string]: { pair1: number; pair2: number };
  }>({});

  const pair1 = pairs.find((p) => p.id === match.pair1_id);
  const pair2 = pairs.find((p) => p.id === match.pair2_id);

  const pair1Name = pair1
    ? `${pair1.player1?.name} y ${pair1.player2?.name}`
    : "Pareja 1";
  const pair2Name = pair2
    ? `${pair2.player1?.name} y ${pair2.player2?.name}`
    : "Pareja 2";

  useEffect(() => {
    loadGames();
  }, [match.id]);

  const loadGames = async () => {
    try {
      setLoading(true);
      const gamesData = await getGames(match.id);
      setGames(gamesData);
    } catch (err) {
      setError("Error al cargar los juegos");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const addGame = async () => {
    try {
      setError("");
      setLoading(true);

      const newGameNumber = games.length + 1;
      const newGame = await createGame(match.id, newGameNumber);

      setGames([...games, newGame]);
      console.log("‚úÖ Juego agregado:", newGame);
    } catch (err) {
      setError("Error al agregar el juego");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const updateGameScore = async (
    gameId: string,
    pair1Games: number,
    pair2Games: number
  ) => {
    try {
      setError("");
      await updateGame(gameId, {
        pair1_games: pair1Games,
        pair2_games: pair2Games,
      });

      setGames((prevGames) =>
        prevGames.map((g) =>
          g.id === gameId
            ? { ...g, pair1_games: pair1Games, pair2_games: pair2Games }
            : g
        )
      );
    } catch (err) {
      setError("Error al actualizar el marcador");
      console.error(err);
    }
  };

  const updateTieBreakScore = async (
    gameId: string,
    pair1Points: number,
    pair2Points: number
  ) => {
    try {
      setError("");
      await updateGame(gameId, {
        tie_break_pair1_points: pair1Points,
        tie_break_pair2_points: pair2Points,
      });

      setGames((prevGames) =>
        prevGames.map((g) =>
          g.id === gameId
            ? {
                ...g,
                tie_break_pair1_points: pair1Points,
                tie_break_pair2_points: pair2Points,
              }
            : g
        )
      );
    } catch (err) {
      setError("Error al actualizar el tie break");
      console.error(err);
    }
  };

  const removeGame = async (gameId: string) => {
    try {
      setError("");
      await deleteGame(gameId);
      setGames(games.filter((g) => g.id !== gameId));
    } catch (err) {
      setError("Error al eliminar el juego");
      console.error(err);
    }
  };

  const toggleTieBreak = async (gameId: string) => {
    try {
      setError("");
      const game = games.find((g) => g.id === gameId);
      if (!game) return;

      const newIsTieBreak = !game.is_tie_break;
      await updateGame(gameId, { is_tie_break: newIsTieBreak });

      setGames((prevGames) =>
        prevGames.map((g) =>
          g.id === gameId ? { ...g, is_tie_break: newIsTieBreak } : g
        )
      );
    } catch (err) {
      setError("Error al cambiar el tipo de juego");
      console.error(err);
    }
  };

  const finishMatch = async () => {
    try {
      setError("");
      setLoading(true);

      const result = await SimpleMatchCalculator.updateMatchStatistics(
        match,
        games,
        pairs
      );

      if (result.success) {
        alert(result.message);
        onUpdate();
        onClose();
      } else {
        setError(result.message);
      }
    } catch (err) {
      setError("Error al finalizar el partido");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleGameScoreChange = (
    gameId: string,
    pair: "pair1" | "pair2",
    value: number
  ) => {
    setEditingGames((prev) => ({
      ...prev,
      [gameId]: {
        ...prev[gameId],
        [pair]: value,
      },
    }));
  };

  const handleGameScoreBlur = async (gameId: string) => {
    const editingGame = editingGames[gameId];
    if (editingGame) {
      await updateGameScore(gameId, editingGame.pair1, editingGame.pair2);
      setEditingGames((prev) => {
        const newState = { ...prev };
        delete newState[gameId];
        return newState;
      });
    }
  };

  const handleTieBreakScoreChange = (
    gameId: string,
    pair: "pair1" | "pair2",
    value: number
  ) => {
    setEditingTieBreaks((prev) => ({
      ...prev,
      [gameId]: {
        ...prev[gameId],
        [pair]: value,
      },
    }));
  };

  const handleTieBreakScoreBlur = async (gameId: string) => {
    const editingTieBreak = editingTieBreaks[gameId];
    if (editingTieBreak) {
      await updateTieBreakScore(
        gameId,
        editingTieBreak.pair1,
        editingTieBreak.pair2
      );
      setEditingTieBreaks((prev) => {
        const newState = { ...prev };
        delete newState[gameId];
        return newState;
      });
    }
  };

  if (loading) {
    return (
      <div className="game-editor-overlay">
        <div className="game-editor-modal">
          <div className="loading">Cargando...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="game-editor-overlay">
      <div className="game-editor-modal">
        <div className="game-editor-header">
          <h3>üéæ Editor de Juegos</h3>
          <button onClick={onClose} className="close-btn">
            ‚úï
          </button>
        </div>

        <div className="match-info">
          <h4>
            {pair1Name} vs {pair2Name}
          </h4>
          <p>
            Cancha {match.court} - Ronda {match.round}
          </p>
        </div>

        {error && <div className="error-message">{error}</div>}

        <div className="games-section">
          <div className="games-header">
            <h5>üéæ Juegos</h5>
            <button
              onClick={addGame}
              disabled={loading}
              className="add-game-btn"
            >
              ‚ûï Agregar Juego
            </button>
          </div>

          {games.map((game) => (
            <div key={game.id} className="game-card">
              <div className="game-header">
                <h6>Juego {game.game_number}</h6>
                <div className="game-actions">
                  <button
                    onClick={() => toggleTieBreak(game.id)}
                    disabled={loading}
                    className="tie-break-btn"
                  >
                    {game.is_tie_break ? "üîó" : "üîì"} Tie-Break
                  </button>
                  <button
                    onClick={() => removeGame(game.id)}
                    disabled={loading}
                    className="remove-game-btn"
                  >
                    üóëÔ∏è Eliminar
                  </button>
                </div>
              </div>

              <div className="game-scores">
                <div className="score-input">
                  <label>{pair1Name}:</label>
                  <input
                    type="number"
                    min="0"
                    max="20"
                    value={
                      editingGames[game.id]?.pair1 !== undefined
                        ? editingGames[game.id].pair1
                        : game.pair1_games
                    }
                    onChange={(e) =>
                      handleGameScoreChange(
                        game.id,
                        "pair1",
                        parseInt(e.target.value) || 0
                      )
                    }
                    onBlur={() => handleGameScoreBlur(game.id)}
                    placeholder="0"
                  />
                </div>

                <div className="score-input">
                  <label>{pair2Name}:</label>
                  <input
                    type="number"
                    min="0"
                    max="20"
                    value={
                      editingGames[game.id]?.pair2 !== undefined
                        ? editingGames[game.id].pair2
                        : game.pair2_games
                    }
                    onChange={(e) =>
                      handleGameScoreChange(
                        game.id,
                        "pair2",
                        parseInt(e.target.value) || 0
                      )
                    }
                    onBlur={() => handleGameScoreBlur(game.id)}
                    placeholder="0"
                  />
                </div>
              </div>

              {game.is_tie_break && (
                <div className="tie-break-scores">
                  <h6>üîó Tie-Break</h6>
                  <div className="score-input">
                    <label>{pair1Name}:</label>
                    <input
                      type="number"
                      min="0"
                      max="20"
                      value={
                        editingTieBreaks[game.id]?.pair1 !== undefined
                          ? editingTieBreaks[game.id].pair1
                          : game.tie_break_pair1_points
                      }
                      onChange={(e) =>
                        handleTieBreakScoreChange(
                          game.id,
                          "pair1",
                          parseInt(e.target.value) || 0
                        )
                      }
                      onBlur={() => handleTieBreakScoreBlur(game.id)}
                      placeholder="0"
                    />
                  </div>

                  <div className="score-input">
                    <label>{pair2Name}:</label>
                    <input
                      type="number"
                      min="0"
                      max="20"
                      value={
                        editingTieBreaks[game.id]?.pair2 !== undefined
                          ? editingTieBreaks[game.id].pair2
                          : game.tie_break_pair2_points
                      }
                      onChange={(e) =>
                        handleTieBreakScoreChange(
                          game.id,
                          "pair2",
                          parseInt(e.target.value) || 0
                        )
                      }
                      onBlur={() => handleTieBreakScoreBlur(game.id)}
                      placeholder="0"
                    />
                  </div>
                </div>
              )}
            </div>
          ))}
        </div>

        <div className="game-editor-actions">
          <button
            onClick={finishMatch}
            disabled={loading || games.length === 0}
            className="finish-match-btn"
          >
            üèÜ Finalizar Partido
          </button>
          <button onClick={onClose} className="cancel-btn">
            ‚ùå Cancelar
          </button>
        </div>
      </div>
    </div>
  );
};

export default GameEditor;
