import React, { useState, useEffect } from "react";
import { Match, Game, Pair } from "../lib/database";
import {
  getGames,
  createGame,
  updateGame,
  deleteGame,
  getPairs,
} from "../lib/database";

interface MatchScoreEditorProps {
  match: Match;
  onClose: () => void;
  onMatchFinish: () => void;
}

export const MatchScoreEditor: React.FC<MatchScoreEditorProps> = ({
  match,
  onClose,
  onMatchFinish,
}) => {
  const [games, setGames] = useState<Game[]>([]);
  const [pairs, setPairs] = useState<Pair[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string>("");

  // Cargar juegos y parejas al montar el componente
  useEffect(() => {
    const loadData = async () => {
      try {
        console.log("üîÑ Cargando datos del editor de marcador...");

        // Cargar juegos del partido
        const matchGames = await getGames(match.id);
        console.log(`üìä Juegos cargados: ${matchGames.length}`);
        setGames(matchGames);

        // Cargar parejas del torneo
        const allPairs = await getPairs(match.tournament_id);
        console.log(`üë• Parejas cargadas: ${allPairs.length}`);
        setPairs(allPairs);
      } catch (err) {
        console.error("‚ùå Error cargando datos:", err);
        setError("Error al cargar los datos");
      }
    };

    loadData();
  }, [match.id, match.tournament_id]);

  // Agregar nuevo juego
  const addGame = async () => {
    try {
      setLoading(true);
      setError("");

      console.log(
        `‚ûï Agregando juego ${games.length + 1} al partido ${match.id}`
      );

      const newGame = await createGame(match.id, games.length + 1);
      console.log(`‚úÖ Juego creado: ${newGame.id}`);

      setGames((prevGames) => {
        const updatedGames = [...prevGames, newGame];
        console.log(
          `üìä Total de juegos despu√©s de agregar: ${updatedGames.length}`
        );
        return updatedGames;
      });
    } catch (err) {
      console.error("‚ùå Error agregando juego:", err);
      setError("Error al agregar juego");
    } finally {
      setLoading(false);
    }
  };

  // CORREGIR puntuaci√≥n de un juego espec√≠fico (NO acumula estad√≠sticas)
  const correctGameScore = async (
    gameId: string,
    pair1Games: number,
    pair2Games: number,
    isTieBreak: boolean = false
  ) => {
    try {
      console.log(
        `üîÑ CORRIGIENDO juego ${gameId}: ${pair1Games}-${pair2Games}`
      );
      console.log(`üìä Juegos antes de corregir: ${games.length}`);

      // SOLO actualizar el juego en la base de datos
      await updateGame(gameId, {
        pair1_games: pair1Games,
        pair2_games: pair2Games,
        is_tie_break: isTieBreak,
      });

      // Actualizar estado local
      setGames((prevGames) => {
        const updatedGames = prevGames.map((game) =>
          game.id === gameId
            ? {
                ...game,
                pair1_games: pair1Games,
                pair2_games: pair2Games,
                is_tie_break: isTieBreak,
              }
            : game
        );

        console.log(`üìä Juegos despu√©s de corregir: ${updatedGames.length}`);
        console.log(
          `‚úÖ Juego ${gameId} corregido correctamente - NO se acumularon estad√≠sticas`
        );

        return updatedGames;
      });

      // CR√çTICO: NO llamar a onMatchFinish() aqu√≠
      // Solo se llama cuando se finaliza el partido
      console.log("üö´ NO se llama onMatchFinish() al corregir juego");
    } catch (err) {
      console.error("‚ùå Error corrigiendo juego:", err);
      setError("Error al corregir puntuaci√≥n");
    }
  };

  // Eliminar juego
  const removeGame = async (gameId: string) => {
    try {
      setLoading(true);
      setError("");

      console.log(`üóëÔ∏è Eliminando juego ${gameId}`);

      await deleteGame(gameId);

      setGames((prevGames) => {
        const updatedGames = prevGames.filter((game) => game.id !== gameId);
        console.log(`üìä Juegos despu√©s de eliminar: ${updatedGames.length}`);
        return updatedGames;
      });

      // CR√çTICO: NO llamar a onMatchFinish() aqu√≠
      console.log("üö´ NO se llama onMatchFinish() al eliminar juego");
    } catch (err) {
      console.error("‚ùå Error eliminando juego:", err);
      setError("Error al eliminar juego");
    } finally {
      setLoading(false);
    }
  };

  // Cambiar tipo de juego (normal/tie-break)
  const toggleTieBreak = async (gameId: string) => {
    const game = games.find((g) => g.id === gameId);
    if (game) {
      await correctGameScore(
        gameId,
        game.pair1_games,
        game.pair2_games,
        !game.is_tie_break
      );
    }
  };

  // FINALIZAR partido (S√ç acumula estad√≠sticas)
  const finishMatch = async () => {
    try {
      setLoading(true);
      setError("");

      console.log("üèÜ FINALIZANDO partido...");

      // Calcular estad√≠sticas del partido
      let pair1TotalPoints = 0;
      let pair2TotalPoints = 0;
      let pair1GamesWon = 0;
      let pair2GamesWon = 0;
      let pair1SetsWon = 0;
      let pair2SetsWon = 0;

      games.forEach((game, index) => {
        console.log(
          `üìä Procesando juego ${index + 1}: ${game.pair1_games}-${
            game.pair2_games
          }`
        );

        if (game.is_tie_break) {
          // Tie-break
          if (game.tie_break_pair1_points > game.tie_break_pair2_points) {
            pair1GamesWon++;
          } else if (
            game.tie_break_pair2_points > game.tie_break_pair1_points
          ) {
            pair2GamesWon++;
          }
          pair1TotalPoints += game.tie_break_pair1_points || 0;
          pair2TotalPoints += game.tie_break_pair2_points || 0;
        } else {
          // Juego normal
          if (game.pair1_games > game.pair2_games) {
            pair1GamesWon++;
          } else if (game.pair2_games > game.pair1_games) {
            pair2GamesWon++;
          }
          pair1TotalPoints += game.pair1_games;
          pair2TotalPoints += game.pair2_games;

          // Sets (6 puntos = 1 set)
          if (game.pair1_games >= 6) {
            pair1SetsWon++;
          }
          if (game.pair2_games >= 6) {
            pair2SetsWon++;
          }
        }
      });

      console.log("üìä Estad√≠sticas finales del partido:");
      console.log(
        `üìä Pareja 1: ${pair1GamesWon} juegos, ${pair1SetsWon} sets, ${pair1TotalPoints} puntos`
      );
      console.log(
        `üìä Pareja 2: ${pair2GamesWon} juegos, ${pair2SetsWon} sets, ${pair2TotalPoints} puntos`
      );

      // ACUMULAR estad√≠sticas en la base de datos
      const pair1 = pairs.find((p) => p.id === match.pair1_id);
      const pair2 = pairs.find((p) => p.id === match.pair2_id);

      if (pair1) {
        console.log(
          `üìä Acumulando Pareja 1: ${pair1.player1?.name} y ${pair1.player2?.name}`
        );
        console.log(
          `üìä Estad√≠sticas actuales: ${pair1.games_won} juegos, ${pair1.sets_won} sets, ${pair1.points} puntos, ${pair1.matches_played} partidos`
        );

        const { updatePair } = await import("../lib/database");
        await updatePair(pair1.id, {
          games_won: pair1.games_won + pair1GamesWon,
          sets_won: pair1.sets_won + pair1SetsWon,
          points: pair1.points + pair1TotalPoints,
          matches_played: pair1.matches_played + 1,
        });

        console.log(
          `‚úÖ Pareja 1 acumulada: ${pair1.games_won + pair1GamesWon} juegos, ${
            pair1.sets_won + pair1SetsWon
          } sets, ${pair1.points + pair1TotalPoints} puntos, ${
            pair1.matches_played + 1
          } partidos`
        );
      }

      if (pair2) {
        console.log(
          `üìä Acumulando Pareja 2: ${pair2.player1?.name} y ${pair2.player2?.name}`
        );
        console.log(
          `üìä Estad√≠sticas actuales: ${pair2.games_won} juegos, ${pair2.sets_won} sets, ${pair2.points} puntos, ${pair2.matches_played} partidos`
        );

        const { updatePair } = await import("../lib/database");
        await updatePair(pair2.id, {
          games_won: pair2.games_won + pair2GamesWon,
          sets_won: pair2.sets_won + pair2SetsWon,
          points: pair2.points + pair2TotalPoints,
          matches_played: pair2.matches_played + 1,
        });

        console.log(
          `‚úÖ Pareja 2 acumulada: ${pair2.games_won + pair2GamesWon} juegos, ${
            pair2.sets_won + pair2SetsWon
          } sets, ${pair2.points + pair2TotalPoints} puntos, ${
            pair2.matches_played + 1
          } partidos`
        );
      }

      // Determinar ganador
      let winnerId: string | undefined;
      const isTie = pair1TotalPoints === pair2TotalPoints;

      if (isTie) {
        winnerId = undefined;
        console.log("ü§ù Partido termin√≥ en EMPATE");
      } else if (pair1TotalPoints > pair2TotalPoints) {
        winnerId = match.pair1_id;
        console.log("üèÜ Pareja 1 gana por puntos totales");
      } else {
        winnerId = match.pair2_id;
        console.log("üèÜ Pareja 2 gana por puntos totales");
      }

      // Marcar partido como finalizado
      const { updateMatch } = await import("../lib/database");
      await updateMatch(match.id, {
        winner_id: winnerId,
        is_finished: true,
      });

      console.log("‚úÖ Partido finalizado correctamente");

      // CR√çTICO: SOLO llamar onMatchFinish() cuando se finaliza el partido
      console.log(
        "üèÜ Llamando onMatchFinish() para actualizar tabla de clasificaci√≥n"
      );
      onMatchFinish();
      onClose();
    } catch (err) {
      console.error("‚ùå Error finalizando partido:", err);
      setError("Error al finalizar partido");
    } finally {
      setLoading(false);
    }
  };

  // Obtener nombres de pareja
  const getPairNames = (pairId: string) => {
    const pair = pairs.find((p) => p.id === pairId);
    if (pair) {
      return `${pair.player1?.name || "Jugador 1"} y ${
        pair.player2?.name || "Jugador 2"
      }`;
    }
    return "Pareja desconocida";
  };

  return (
    <div className="match-score-editor-overlay">
      <div className="match-score-editor-modal">
        {/* Header */}
        <div className="match-score-editor-header">
          <div className="match-score-editor-title">
            <span className="match-score-editor-icon">üéæ</span>
            <h3>Editor de Marcador</h3>
          </div>
          <button className="match-score-editor-close-btn" onClick={onClose}>
            ‚úï
          </button>
        </div>

        {/* Contenido */}
        <div className="match-score-editor-content">
          {/* Informaci√≥n del partido */}
          <div className="match-score-editor-info">
            <h4>
              Partido {match.court} - Ronda {match.round}
            </h4>
            <div className="match-score-editor-pairs">
              <div className="match-score-editor-pair">
                <span className="match-score-editor-pair-label">
                  üèÜ Pareja 1:
                </span>
                <span className="match-score-editor-pair-names">
                  {getPairNames(match.pair1_id)}
                </span>
              </div>
              <div className="match-score-editor-pair">
                <span className="match-score-editor-pair-label">
                  üèÜ Pareja 2:
                </span>
                <span className="match-score-editor-pair-names">
                  {getPairNames(match.pair2_id)}
                </span>
              </div>
            </div>
          </div>

          {/* Reglas */}
          <div className="match-score-editor-rules">
            <h5>üìã Reglas del Juego:</h5>
            <ul>
              <li>
                <strong>Juegos:</strong> La pareja que haga m√°s puntos gana el
                juego
              </li>
              <li>
                <strong>Sets:</strong> La pareja que llegue a 6 puntos gana 1
                set
              </li>
              <li>
                <strong>Empates:</strong> Si ambas parejas tienen los mismos
                puntos, es empate
              </li>
              <li>
                <strong>Tie-Breaks:</strong> Se pueden activar para desempates
                especiales
              </li>
            </ul>
          </div>

          {/* Secci√≥n de juegos */}
          <div className="match-score-editor-games-section">
            <div className="match-score-editor-games-header">
              <h5>üéæ Juegos ({games.length})</h5>
              <button
                className="match-score-editor-add-game-btn"
                onClick={addGame}
                disabled={loading}
              >
                + Agregar Juego
              </button>
            </div>

            {games.length === 0 ? (
              <div className="match-score-editor-no-games">
                <p>
                  No hay juegos registrados. Haz clic en "Agregar Juego" para
                  comenzar.
                </p>
              </div>
            ) : (
              <div className="match-score-editor-games-list">
                {games.map((game, index) => (
                  <div key={game.id} className="match-score-editor-game-card">
                    <div className="match-score-editor-game-header">
                      <h6>Juego {index + 1}</h6>
                      <div className="match-score-editor-game-actions">
                        <button
                          className="match-score-editor-tie-break-btn"
                          onClick={() => toggleTieBreak(game.id)}
                        >
                          {game.is_tie_break ? "üîó Tie-Break" : "üîó Normal"}
                        </button>
                        <button
                          className="match-score-editor-remove-game-btn"
                          onClick={() => removeGame(game.id)}
                        >
                          üóëÔ∏è Eliminar
                        </button>
                      </div>
                    </div>

                    <div className="match-score-editor-game-scores">
                      <div className="match-score-editor-score-input">
                        <label className="match-score-editor-pair-name-label">
                          {getPairNames(match.pair1_id)}
                        </label>
                        <input
                          type="number"
                          min="0"
                          value={game.pair1_games}
                          onChange={(e) =>
                            correctGameScore(
                              game.id,
                              parseInt(e.target.value) || 0,
                              game.pair2_games,
                              game.is_tie_break
                            )
                          }
                        />
                      </div>

                      <div className="match-score-editor-score-separator">
                        -
                      </div>

                      <div className="match-score-editor-score-input">
                        <label className="match-score-editor-pair-name-label">
                          {getPairNames(match.pair2_id)}
                        </label>
                        <input
                          type="number"
                          min="0"
                          value={game.pair2_games}
                          onChange={(e) =>
                            correctGameScore(
                              game.id,
                              game.pair1_games,
                              parseInt(e.target.value) || 0,
                              game.is_tie_break
                            )
                          }
                        />
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Mensaje de error */}
          {error && (
            <div className="match-score-editor-error">
              <p>‚ùå {error}</p>
            </div>
          )}

          {/* Botones de acci√≥n */}
          <div className="match-score-editor-actions">
            <button
              className="match-score-editor-finish-btn"
              onClick={finishMatch}
              disabled={loading || games.length === 0}
            >
              {loading ? "‚è≥ Finalizando..." : "üèÜ Finalizar Partido"}
            </button>
            <button className="match-score-editor-cancel-btn" onClick={onClose}>
              ‚ùå Cancelar
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};
