import React, { useState, useEffect } from "react";
import {
  createPlayer,
  getPlayers,
  deletePlayer,
  Player,
} from "../lib/database";

interface PlayerManagerProps {
  onPlayerSelect?: (players: Player[]) => void;
  selectedPlayers?: Player[];
  allowMultipleSelection?: boolean;
  playersInPairs?: string[]; // IDs de jugadores que ya est√°n en parejas
}

export const PlayerManager: React.FC<PlayerManagerProps> = ({
  onPlayerSelect,
  selectedPlayers = [],
  allowMultipleSelection = false,
  playersInPairs = [],
}) => {
  const [players, setPlayers] = useState<Player[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string>("");
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [newPlayerName, setNewPlayerName] = useState("");

  useEffect(() => {
    loadPlayers();
  }, []);

  const loadPlayers = async () => {
    try {
      setLoading(true);
      const data = await getPlayers();
      setPlayers(data);
    } catch (err) {
      setError("Error al cargar los jugadores");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleCreatePlayer = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!newPlayerName.trim()) return;

    try {
      setError("");
      const player = await createPlayer(newPlayerName.trim());
      setPlayers([...players, player]);
      setNewPlayerName("");
      setShowCreateForm(false);
    } catch (err) {
      setError("Error al crear el jugador");
      console.error(err);
    }
  };

  const handleDeletePlayer = async (id: string) => {
    if (
      !window.confirm(
        "¬øEst√°s seguro de que quieres eliminar este jugador? Esta acci√≥n no se puede deshacer."
      )
    ) {
      return;
    }

    try {
      setError("");
      await deletePlayer(id);
      setPlayers(players.filter((p) => p.id !== id));
    } catch (err) {
      setError("Error al eliminar el jugador");
      console.error(err);
    }
  };

  const handlePlayerSelect = (player: Player) => {
    // Verificar si el jugador ya est√° en una pareja
    if (playersInPairs.includes(player.id)) {
      console.log(`üö® JUGADOR BLOQUEADO: ${player.name} ya est√° en una pareja`);
      alert(
        `No puedes seleccionar a ${player.name} porque ya est√° en una pareja. Debes eliminar su pareja actual primero.`
      );
      return;
    }

    if (onPlayerSelect) {
      if (allowMultipleSelection) {
        const isSelected = selectedPlayers.some((p) => p.id === player.id);
        if (isSelected) {
          // Deseleccionar el jugador si ya est√° seleccionado
          onPlayerSelect(selectedPlayers.filter((p) => p.id !== player.id));
        } else {
          onPlayerSelect([...selectedPlayers, player]);
        }
      } else {
        onPlayerSelect([player]);
      }
    }
  };

  const isPlayerSelected = (player: Player) => {
    return selectedPlayers.some((p) => p.id === player.id);
  };

  const isPlayerInPair = (player: Player) => {
    return playersInPairs.includes(player.id);
  };

  if (loading) {
    return <div className="loading">Cargando jugadores...</div>;
  }

  return (
    <div className="player-manager">
      <div className="player-header">
        <h3>üë• Gesti√≥n de Jugadores</h3>
        <button
          onClick={() => setShowCreateForm(!showCreateForm)}
          className="create-player-btn"
        >
          {showCreateForm ? "‚ùå Cancelar" : "‚ûï Agregar Jugador"}
        </button>
      </div>

      {error && <div className="error">{error}</div>}

      {showCreateForm && (
        <div className="create-player-form">
          <h4>Agregar Nuevo Jugador</h4>
          <form onSubmit={handleCreatePlayer}>
            <div className="form-group">
              <label htmlFor="player-name">Nombre del Jugador:</label>
              <input
                id="player-name"
                type="text"
                value={newPlayerName}
                onChange={(e) => setNewPlayerName(e.target.value)}
                placeholder="Ej: Juan P√©rez"
                required
                autoFocus
              />
            </div>

            <div className="form-actions">
              <button type="submit" className="submit-btn">
                ‚úÖ Agregar Jugador
              </button>
              <button
                type="button"
                onClick={() => {
                  setShowCreateForm(false);
                  setNewPlayerName("");
                }}
                className="cancel-btn"
              >
                ‚ùå Cancelar
              </button>
            </div>
          </form>
        </div>
      )}

      <div className="players-list">
        <h4>Jugadores Registrados ({players.length})</h4>
        {players.length === 0 ? (
          <div className="no-players">
            <p>üìù No hay jugadores registrados a√∫n</p>
            <p>Agrega jugadores para poder crear parejas</p>
          </div>
        ) : (
          <div className="players-grid">
            {players.map((player) => (
              <div
                key={player.id}
                className={`player-card ${
                  isPlayerSelected(player) ? "selected" : ""
                } ${isPlayerInPair(player) ? "in-pair" : ""}`}
                title={
                  isPlayerInPair(player)
                    ? `${player.name} ya est√° en una pareja. Elimina su pareja actual para poder seleccionarlo.`
                    : isPlayerSelected(player)
                    ? "Haz clic para deseleccionar"
                    : "Haz clic para seleccionar"
                }
              >
                <div className="player-info">
                  <span className="player-name">{player.name}</span>
                  <span className="player-date">
                    Registrado:{" "}
                    {new Date(player.created_at).toLocaleDateString("es-ES")}
                  </span>
                </div>

                <div className="player-actions">
                  {onPlayerSelect && (
                    <button
                      onClick={() => handlePlayerSelect(player)}
                      className={`select-btn ${
                        isPlayerSelected(player) ? "selected" : ""
                      } ${isPlayerInPair(player) ? "disabled" : ""}`}
                      disabled={isPlayerInPair(player)}
                    >
                      {isPlayerInPair(player)
                        ? "üö´ Bloqueado"
                        : isPlayerSelected(player)
                        ? "‚ùå Deseleccionar"
                        : "üëÜ Seleccionar"}
                    </button>
                  )}

                  <button
                    onClick={() => handleDeletePlayer(player.id)}
                    className="delete-btn"
                    title="Eliminar jugador"
                  >
                    üóëÔ∏è
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};
