      console.log("üìä Datos del juego:", JSON.stringify(game, null, 2));

      if (game.is_tie_break) {
        console.log("üìä Es un tie-break");
        // Para tie-breaks
        if (
          game.tie_break_pair1_points >= 10 &&
          game.tie_break_pair1_points - game.tie_break_pair2_points >= 2
        ) {
          pair1GamesWon++;
          console.log(
            `‚úÖ Pareja 1 gana tie-break: ${game.tie_break_pair1_points}-${game.tie_break_pair2_points}`
          );
        } else if (
          game.tie_break_pair2_points >= 10 &&
          game.tie_break_pair2_points - game.tie_break_pair1_points >= 2
