          pair2GamesWon++;
          console.log(
            `‚úÖ Pareja 2 gana tie-break: ${game.tie_break_pair1_points}-${game.tie_break_pair2_points}`
          );
        } else {
          // Empate en tie-break - ninguna gana juego (mantener como estaba)
          console.log(
            `ü§ù Empate en tie-break: ${game.tie_break_pair1_points}-${game.tie_break_pair2_points} - ninguna gana juego`
          );
        }
        pair1TotalPoints += game.tie_break_pair1_points;
        pair2TotalPoints += game.tie_break_pair2_points;
