    console.log("üìä === RESULTADO FINAL ===");
    console.log(`üìä Juegos: ${pair1GamesWon}-${pair2GamesWon}`);
    console.log(`üìä Sets: ${pair1SetsWon}-${pair2SetsWon}`);
    console.log(`üìä Puntos totales: ${pair1TotalPoints}-${pair2TotalPoints}`);
    console.log(`üìä Es empate: ${isTie}`);
    console.log("üéØ === FIN C√ÅLCULO DE ESTAD√çSTICAS ===");

    return {
