  }

  /**
   * Recalcula TODAS las estad√≠sticas desde cero para un torneo
   */
  static async recalculateAllStatistics(tournamentId: string): Promise<{
    success: boolean;
    message: string;
  }> {
    try {
      console.log("üîÑ === INICIO REC√ÅLCULO COMPLETO ===");
      console.log("üîÑ Recalculando TODAS las estad√≠sticas desde cero...");

      // Obtener todas las parejas del torneo
      const pairs = await getPairs(tournamentId);
      console.log(`üìä Total de parejas: ${pairs.length}`);

      // Resetear TODAS las estad√≠sticas a 0
      console.log("üìä Reseteando estad√≠sticas de todas las parejas...");
      for (const pair of pairs) {
        await updatePair(pair.id, {
          games_won: 0,
          sets_won: 0,
          points: 0,
          matches_played: 0,
        });
        console.log(
          `‚úÖ Pareja ${pair.player1?.name} y ${pair.player2?.name} reseteada`
        );
      }

      console.log("‚úÖ Todas las estad√≠sticas reseteadas a 0");

      // Obtener todos los partidos del torneo
      const matches = await getMatches(tournamentId);
      console.log(`üìã Total de partidos: ${matches.length}`);
      console.log(
        `üìã Partidos finalizados: ${
          matches.filter((m) => m.is_finished).length
        }`
      );

      // Contadores acumulados para cada pareja
      const pairStats: {
        [pairId: string]: {
          games: number;
          sets: number;
          points: number;
          matches: number;
        };
      } = {};

      // Inicializar contadores
      pairs.forEach((pair) => {
        pairStats[pair.id] = { games: 0, sets: 0, points: 0, matches: 0 };
      });

      // Recalcular estad√≠sticas para cada partido finalizado
      let processedMatches = 0;
      for (const match of matches) {
        if (match.is_finished) {
          console.log(`üéØ === PROCESANDO PARTIDO ${match.id} ===`);
          const games = await getGames(match.id);
          console.log(`üìä Partido tiene ${games.length} juegos`);

          const stats = this.calculateMatchStatistics(match, games);
          console.log(
            `üìà Estad√≠sticas calculadas: ${stats.pair1GamesWon}-${stats.pair2GamesWon} juegos, ${stats.pair1SetsWon}-${stats.pair2SetsWon} sets`
          );

          // Acumular estad√≠sticas para pareja 1
          if (pairStats[match.pair1_id]) {
            const beforeGames = pairStats[match.pair1_id].games;
            const beforeSets = pairStats[match.pair1_id].sets;

            pairStats[match.pair1_id].games += stats.pair1GamesWon;
            pairStats[match.pair1_id].sets += stats.pair1SetsWon;
            pairStats[match.pair1_id].points += stats.pair1TotalPoints;
            pairStats[match.pair1_id].matches += 1;

            console.log(
              `üìä Pareja 1 acumulado: ${beforeGames}‚Üí${
                pairStats[match.pair1_id].games
              } juegos, ${beforeSets}‚Üí${pairStats[match.pair1_id].sets} sets`
            );
          }

          // Acumular estad√≠sticas para pareja 2
          if (pairStats[match.pair2_id]) {
            const beforeGames = pairStats[match.pair2_id].games;
            const beforeSets = pairStats[match.pair2_id].sets;

            pairStats[match.pair2_id].games += stats.pair2GamesWon;
            pairStats[match.pair2_id].sets += stats.pair2SetsWon;
            pairStats[match.pair2_id].points += stats.pair2TotalPoints;
            pairStats[match.pair2_id].matches += 1;

            console.log(
              `üìä Pareja 2 acumulado: ${beforeGames}‚Üí${
                pairStats[match.pair2_id].games
              } juegos, ${beforeSets}‚Üí${pairStats[match.pair2_id].sets} sets`
            );
          }

          console.log(
            `‚úÖ Partido ${match.id} procesado: ${stats.pair1GamesWon}-${stats.pair2GamesWon} juegos, ${stats.pair1SetsWon}-${stats.pair2SetsWon} sets, Empate: ${stats.isTie}`
          );
          processedMatches++;
        }
      }

      // Actualizar todas las parejas con las estad√≠sticas finales
      console.log("üìä === ACTUALIZANDO ESTAD√çSTICAS FINALES ===");
      for (const pair of pairs) {
        const stats = pairStats[pair.id];
        if (stats) {
          console.log(
            `üìä Actualizando ${pair.player1?.name} y ${pair.player2?.name}:`
          );
          console.log(
            `üìä Juegos: ${stats.games}, Sets: ${stats.sets}, Puntos: ${stats.points}, Partidos: ${stats.matches}`
          );

          await updatePair(pair.id, {
            games_won: stats.games,
            sets_won: stats.sets,
            points: stats.points,
            matches_played: stats.matches,
          });
          console.log(
            `‚úÖ ${pair.player1?.name} y ${pair.player2?.name}: ${stats.games} juegos, ${stats.sets} sets, ${stats.points} puntos totales, ${stats.matches} partidos`
          );
        }
      }

      console.log(
        `‚úÖ Recalculaci√≥n completa: ${processedMatches} partidos procesados`
      );
      console.log("üîÑ === FIN REC√ÅLCULO COMPLETO ===");
      return {
        success: true,
        message: `‚úÖ Estad√≠sticas recalculadas correctamente. ${processedMatches} partidos procesados.`,
      };
    } catch (error) {
      console.error("‚ùå Error recalculando estad√≠sticas:", error);
      return {
        success: false,
        message: `Error recalculando estad√≠sticas: ${error}`,
      };
    }
  }

  /**
   * Calcula el ranking de parejas basado en SETS GANADOS como criterio principal
   */
  static calculateRanking(pairs: Pair[]): Pair[] {
    return [...pairs].sort((a, b) => {
      // Criterio 1: Sets ganados (descendente) - CRITERIO PRINCIPAL
      if (b.sets_won !== a.sets_won) {
        return b.sets_won - a.sets_won;
      }

      // Criterio 2: Puntos totales (descendente)
      if (b.points !== a.points) {
        return b.points - a.points;
      }

      // Criterio 3: Menos partidos jugados (mejor eficiencia)
      return a.matches_played - b.matches_played;
    });
  }
