import { Game, Match, Pair } from "../lib/database";
import { updatePair, getPairs, getMatches, getGames } from "../lib/database";

export interface MatchResult {
  success: boolean;
  message: string;
  pair1Stats: {
    gamesWon: number;
    setsWon: number;
    points: number;
  };
  pair2Stats: {
    gamesWon: number;
    setsWon: number;
    points: number;
  };
  winnerId?: string;
  isTie: boolean;
}

export class MatchResultCalculator {
  /**
   * Calcula estad√≠sticas de un partido
   */
  static calculateMatchStatistics(
    match: Match,
    games: Game[]
  ): {
    pair1GamesWon: number;
    pair2GamesWon: number;
    pair1SetsWon: number;
    pair2SetsWon: number;
    pair1TotalPoints: number;
    pair2TotalPoints: number;
    isTie: boolean;
  } {
    console.log("üéØ === C√ÅLCULO DE ESTAD√çSTICAS ===");
    console.log("üéØ Partido ID:", match.id);
    console.log("üéØ Total de juegos:", games.length);

    let pair1GamesWon = 0;
    let pair2GamesWon = 0;
    let pair1TotalPoints = 0;
    let pair2TotalPoints = 0;
    let pair1SetsWon = 0;
    let pair2SetsWon = 0;

    // Procesar cada juego
    games.forEach((game, index) => {
      console.log(`üìä === JUEGO ${index + 1} ===`);
      console.log("üìä Datos del juego:", JSON.stringify(game, null, 2));

      if (game.is_tie_break) {
        console.log("üìä Es un tie-break");
        // Para tie-breaks
        if (game.tie_break_pair1_points > game.tie_break_pair2_points) {
          pair1GamesWon++;
          console.log(
            `‚úÖ Pareja 1 gana tie-break: ${game.tie_break_pair1_points}-${game.tie_break_pair2_points}`
          );
        } else if (game.tie_break_pair2_points > game.tie_break_pair1_points) {
          pair2GamesWon++;
          console.log(
            `‚úÖ Pareja 2 gana tie-break: ${game.tie_break_pair1_points}-${game.tie_break_pair2_points}`
          );
        } else {
          console.log(
            `ü§ù Empate en tie-break: ${game.tie_break_pair1_points}-${game.tie_break_pair2_points}`
          );
        }
        pair1TotalPoints += game.tie_break_pair1_points;
        pair2TotalPoints += game.tie_break_pair2_points;
      } else {
        console.log("üìä Es un juego normal");
        console.log(
          `üìä Puntos Pareja 1: ${game.pair1_games}, Puntos Pareja 2: ${game.pair2_games}`
        );

        // Para juegos normales
        if (game.pair1_games > game.pair2_games) {
          pair1GamesWon++;
          console.log(
            `‚úÖ Pareja 1 gana juego: ${game.pair1_games}-${game.pair2_games}`
          );
        } else if (game.pair2_games > game.pair1_games) {
          pair2GamesWon++;
          console.log(
            `‚úÖ Pareja 2 gana juego: ${game.pair1_games}-${game.pair2_games}`
          );
        } else {
          console.log(
            `ü§ù Empate en juego: ${game.pair1_games}-${game.pair2_games}`
          );
        }
        pair1TotalPoints += game.pair1_games;
        pair2TotalPoints += game.pair2_games;
      }

      // Verificar si alguna pareja lleg√≥ a 6 puntos en este juego (gana 1 set)
      if (!game.is_tie_break) {
        // La pareja que llegue a 6 puntos gana 1 set (sin importar el marcador del oponente)
        if (game.pair1_games >= 6) {
          pair1SetsWon++;
          console.log(
            `üèÜ Pareja 1 gana 1 SET en juego ${index + 1} con ${
              game.pair1_games
            } puntos`
          );
        }
        if (game.pair2_games >= 6) {
          pair2SetsWon++;
          console.log(
            `üèÜ Pareja 2 gana 1 SET en juego ${index + 1} con ${
              game.pair2_games
            } puntos`
          );
        }
      }

      console.log(
        `üìä Despu√©s del juego ${
          index + 1
        }: Pareja 1 = ${pair1GamesWon} juegos, Pareja 2 = ${pair2GamesWon} juegos`
      );
    });

    console.log("üîç === RESULTADO FINAL ===");
    console.log(
      `üîç Juegos: Pareja 1 = ${pair1GamesWon}, Pareja 2 = ${pair2GamesWon}`
    );
    console.log(
      `üîç Sets: Pareja 1 = ${pair1SetsWon}, Pareja 2 = ${pair2SetsWon}`
    );
    console.log(
      `üîç Puntos totales: Pareja 1 = ${pair1TotalPoints}, Pareja 2 = ${pair2TotalPoints}`
    );

    // Determinar si es empate (cuando los puntos totales son iguales)
    const isTie = pair1TotalPoints === pair2TotalPoints;
    console.log(
      `üîç Es empate: ${isTie} (${pair1TotalPoints}-${pair2TotalPoints} puntos totales)`
    );

    console.log("üéØ === FIN C√ÅLCULO ===");

    return {
      pair1GamesWon,
      pair2GamesWon,
      pair1SetsWon,
      pair2SetsWon,
      pair1TotalPoints,
      pair2TotalPoints,
      isTie,
    };
  }

  /**
   * Actualiza estad√≠sticas de un partido espec√≠fico
   */
  static async updateMatchStatistics(
    match: Match,
    games: Game[],
    pairs: Pair[],
    shouldAccumulate: boolean = true
  ): Promise<MatchResult> {
    try {
      console.log("üîÑ === INICIO ACTUALIZACI√ìN ===");

      const stats = this.calculateMatchStatistics(match, games);
      const pair1 = pairs.find((p) => p.id === match.pair1_id);
      const pair2 = pairs.find((p) => p.id === match.pair2_id);

      console.log("üìä Actualizando estad√≠sticas del partido:", {
        pair1GamesWon: stats.pair1GamesWon,
        pair2GamesWon: stats.pair2GamesWon,
        pair1SetsWon: stats.pair1SetsWon,
        pair2SetsWon: stats.pair2SetsWon,
        isTie: stats.isTie,
      });

      // ACTUALIZAR ESTAD√çSTICAS EN LA BASE DE DATOS
      if (pair1) {
        console.log(
          `üìä Actualizando Pareja 1: ${pair1.player1?.name} y ${pair1.player2?.name}`
        );
        console.log(
          `üìä Estad√≠sticas actuales: ${pair1.games_won} juegos, ${pair1.sets_won} sets, ${pair1.points} puntos, ${pair1.matches_played} partidos`
        );
        console.log(
          `üìä Estad√≠sticas del partido: ${stats.pair1GamesWon} juegos, ${stats.pair1SetsWon} sets, ${stats.pair1TotalPoints} puntos`
        );
        console.log(
          `üìä Modo: ${shouldAccumulate ? "Acumular" : "Solo recalcular"}`
        );

        if (shouldAccumulate) {
          // ACUMULAR estad√≠sticas (solo al finalizar partido)
          await updatePair(pair1.id, {
            games_won: pair1.games_won + stats.pair1GamesWon,
            sets_won: pair1.sets_won + stats.pair1SetsWon,
            points: pair1.points + stats.pair1TotalPoints,
            matches_played: pair1.matches_played + 1,
          });

          console.log(
            `‚úÖ Pareja 1 actualizada (ACUMULADO): ${
              pair1.games_won + stats.pair1GamesWon
            } juegos, ${pair1.sets_won + stats.pair1SetsWon} sets, ${
              pair1.points + stats.pair1TotalPoints
            } puntos, ${pair1.matches_played + 1} partidos`
          );
        } else {
          // SOLO RECALCULAR estad√≠sticas (al corregir juegos)
          console.log(
            `‚úÖ Pareja 1: Solo rec√°lculo de estad√≠sticas (sin acumular)`
          );
        }
      }

      if (pair2) {
        console.log(
          `üìä Actualizando Pareja 2: ${pair2.player1?.name} y ${pair2.player2?.name}`
        );
        console.log(
          `üìä Estad√≠sticas actuales: ${pair2.games_won} juegos, ${pair2.sets_won} sets, ${pair2.points} puntos, ${pair2.matches_played} partidos`
        );
        console.log(
          `üìä Estad√≠sticas del partido: ${stats.pair2GamesWon} juegos, ${stats.pair2SetsWon} sets, ${stats.pair2TotalPoints} puntos`
        );
        console.log(
          `üìä Modo: ${shouldAccumulate ? "Acumular" : "Solo recalcular"}`
        );

        if (shouldAccumulate) {
          // ACUMULAR estad√≠sticas (solo al finalizar partido)
          await updatePair(pair2.id, {
            games_won: pair2.games_won + stats.pair2GamesWon,
            sets_won: pair2.sets_won + stats.pair2SetsWon,
            points: pair2.points + stats.pair2TotalPoints,
            matches_played: pair2.matches_played + 1,
          });

          console.log(
            `‚úÖ Pareja 2 actualizada (ACUMULADO): ${
              pair2.games_won + stats.pair2GamesWon
            } juegos, ${pair2.sets_won + stats.pair2SetsWon} sets, ${
              pair2.points + stats.pair2TotalPoints
            } puntos, ${pair2.matches_played + 1} partidos`
          );
        } else {
          // SOLO RECALCULAR estad√≠sticas (al corregir juegos)
          console.log(
            `‚úÖ Pareja 2: Solo rec√°lculo de estad√≠sticas (sin acumular)`
          );
        }
      }

      // Determinar ganador
      let winnerId: string | undefined;

      console.log("üèÜ === DETERMINACI√ìN DEL GANADOR ===");
      console.log(
        `üèÜ Sets: Pareja 1 = ${stats.pair1SetsWon}, Pareja 2 = ${stats.pair2SetsWon}`
      );
      console.log(
        `üèÜ Juegos: Pareja 1 = ${stats.pair1GamesWon}, Pareja 2 = ${stats.pair2GamesWon}`
      );
      console.log(`üèÜ Es empate: ${stats.isTie}`);

      if (stats.isTie) {
        // Es un empate por puntos totales
        winnerId = undefined;
        console.log("ü§ù Partido termin√≥ en EMPATE por puntos totales");
      } else if (stats.pair1TotalPoints > stats.pair2TotalPoints) {
        // Pareja 1 tiene m√°s puntos totales
        winnerId = match.pair1_id;
        console.log("üèÜ Pareja 1 gana por puntos totales");
      } else if (stats.pair2TotalPoints > stats.pair1TotalPoints) {
        // Pareja 2 tiene m√°s puntos totales
        winnerId = match.pair2_id;
        console.log("üèÜ Pareja 2 gana por puntos totales");
      } else {
        // Caso inesperado
        winnerId = undefined;
        console.log("‚ùå Caso inesperado en determinaci√≥n del ganador");
      }

      let message: string;
      if (stats.isTie) {
        message = `‚úÖ Partido finalizado.\n\nü§ù EMPATE (${stats.pair1TotalPoints}-${stats.pair2TotalPoints} puntos totales)`;
      } else if (winnerId) {
        const winnerName =
          winnerId === match.pair1_id
            ? `${pair1?.player1?.name} y ${pair1?.player2?.name}`
            : `${pair2?.player1?.name} y ${pair2?.player2?.name}`;
        message = `‚úÖ Partido finalizado.\n\nüèÜ Ganador: ${winnerName} (${stats.pair1TotalPoints}-${stats.pair2TotalPoints} puntos totales)`;
      } else {
        message = `‚úÖ Partido finalizado.\n\nü§ù Empate (${stats.pair1TotalPoints}-${stats.pair2TotalPoints} puntos totales)`;
      }

      console.log("üîÑ === FIN ACTUALIZACI√ìN ===");

      return {
        success: true,
        message,
        pair1Stats: {
          gamesWon: stats.pair1GamesWon,
          setsWon: stats.pair1SetsWon,
          points: stats.pair1TotalPoints,
        },
        pair2Stats: {
          gamesWon: stats.pair2GamesWon,
          setsWon: stats.pair2SetsWon,
          points: stats.pair2TotalPoints,
        },
        winnerId,
        isTie: stats.isTie,
      };
    } catch (error) {
      console.error("‚ùå Error en updateMatchStatistics:", error);
      return {
        success: false,
        message: "Error al actualizar estad√≠sticas del partido",
        pair1Stats: { gamesWon: 0, setsWon: 0, points: 0 },
        pair2Stats: { gamesWon: 0, setsWon: 0, points: 0 },
        isTie: false,
      };
    }
  }

  /**
   * Recalcula todas las estad√≠sticas del torneo
   */
  static async recalculateAllStatistics(tournamentId: string): Promise<{
    success: boolean;
    message: string;
  }> {
    try {
      console.log("üîÑ === INICIO REC√ÅLCULO ===");

      // Obtener todos los datos del torneo
      const pairs = await getPairs(tournamentId);
      const matches = await getMatches(tournamentId);

      // Resetear todas las estad√≠sticas de las parejas
      console.log("üîÑ Reseteando estad√≠sticas de todas las parejas...");
      for (const pair of pairs) {
        await updatePair(pair.id, {
          games_won: 0,
          sets_won: 0,
          points: 0,
          matches_played: 0,
        });
      }

      // Crear un mapa para acumular estad√≠sticas de cada pareja
      const pairStats = new Map<
        string,
        {
          gamesWon: number;
          setsWon: number;
          points: number;
          matchesPlayed: number;
        }
      >();

      // Inicializar estad√≠sticas para todas las parejas
      for (const pair of pairs) {
        pairStats.set(pair.id, {
          gamesWon: 0,
          setsWon: 0,
          points: 0,
          matchesPlayed: 0,
        });
      }

      // Procesar cada partido finalizado y acumular estad√≠sticas
      let processedMatches = 0;
      for (const match of matches) {
        if (match.is_finished) {
          console.log(`üîÑ Procesando partido: ${match.id}`);
          const games = await getGames(match.id);

          if (games.length > 0) {
            const stats = this.calculateMatchStatistics(match, games);

            // Acumular estad√≠sticas para pareja 1
            const pair1Stats = pairStats.get(match.pair1_id)!;
            pair1Stats.gamesWon += stats.pair1GamesWon;
            pair1Stats.setsWon += stats.pair1SetsWon;
            pair1Stats.points += stats.pair1TotalPoints;
            pair1Stats.matchesPlayed += 1;

            // Acumular estad√≠sticas para pareja 2
            const pair2Stats = pairStats.get(match.pair2_id)!;
            pair2Stats.gamesWon += stats.pair2GamesWon;
            pair2Stats.setsWon += stats.pair2SetsWon;
            pair2Stats.points += stats.pair2TotalPoints;
            pair2Stats.matchesPlayed += 1;

            processedMatches++;
          }
        }
      }

      // Actualizar todas las parejas con sus estad√≠sticas acumuladas
      console.log("üîÑ Actualizando estad√≠sticas acumuladas...");
      for (const pair of pairs) {
        const stats = pairStats.get(pair.id);
        if (stats) {
          await updatePair(pair.id, {
            games_won: stats.gamesWon,
            sets_won: stats.setsWon,
            points: stats.points,
            matches_played: stats.matchesPlayed,
          });
          console.log(
            `üìä Pareja ${pair.id}: ${stats.points} puntos, ${stats.setsWon} sets, ${stats.gamesWon} juegos`
          );
        }
      }

      console.log(
        `‚úÖ Estad√≠sticas recalculadas para ${processedMatches} partidos`
      );
      console.log("üîÑ === FIN REC√ÅLCULO ===");

      return {
        success: true,
        message: `‚úÖ Estad√≠sticas recalculadas exitosamente para ${processedMatches} partidos`,
      };
    } catch (error) {
      console.error("‚ùå Error en recalculateAllStatistics:", error);
      return {
        success: false,
        message: "Error al recalcular estad√≠sticas del torneo",
      };
    }
  }

  /**
   * Calcula el ranking de las parejas
   */
  static calculateRanking(pairs: Pair[]): Pair[] {
    return [...pairs].sort((a, b) => {
      // Criterio 1: Puntos totales (descendente) - CRITERIO PRINCIPAL
      if (a.points !== b.points) {
        return b.points - a.points;
      }
      // Criterio 2: Sets ganados (descendente) - CRITERIO DE DESEMPATE
      if (a.sets_won !== b.sets_won) {
        return b.sets_won - a.sets_won;
      }
      // Criterio 3: Juegos ganados (descendente)
      if (a.games_won !== b.games_won) {
        return b.games_won - a.games_won;
      }
      // Criterio 4: Menos partidos jugados (mejor eficiencia)
      return a.matches_played - b.matches_played;
    });
  }
}
