import React, { useState, useEffect } from "react";
import {
  createGame,
  getGames,
  updateGame,
  deleteGame,
  updateMatch,
  getMatches,
  getPairs,
  updatePair,
  Game as DatabaseGame,
  Match as DatabaseMatch,
  Pair,
} from "../lib/database";
import { MatchResultCalculator } from "./MatchResultCalculator";
import { MatchFinisher } from "./MatchFinisher";
import { FlexibleMatchFinisher } from "./FlexibleMatchFinisher";

// Usar los tipos de la base de datos
type Game = DatabaseGame;
type Match = DatabaseMatch;

interface ScoreCorrectorProps {
  match: Match;
  onClose: () => void;
  onUpdate: () => void;
}

const ScoreCorrector: React.FC<ScoreCorrectorProps> = ({
  match,
  onClose,
  onUpdate,
}) => {
  const [games, setGames] = useState<Game[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [editingGames, setEditingGames] = useState<{
    [key: string]: { pair1: number; pair2: number };
  }>({});
  const [editingTieBreaks, setEditingTieBreaks] = useState<{
    [key: string]: { pair1: number; pair2: number };
  }>({});

  // Cargar juegos del partido
  useEffect(() => {
    loadGames();
  }, [match.id]);

  const loadGames = async () => {
    try {
      setLoading(true);
      const matchGames = await getGames(match.id);
      setGames(matchGames);
      console.log("Juegos cargados:", matchGames);
    } catch (error) {
      console.error("Error cargando juegos:", error);
      setError("Error al cargar los juegos");
    } finally {
      setLoading(false);
    }
  };

  const addGame = async () => {
    try {
      setLoading(true);
      const gameNumber = games.length + 1;
      const newGame = await createGame(match.id, gameNumber);
      setGames([...games, newGame]);
      console.log("Nuevo juego creado:", newGame);
    } catch (error) {
      console.error("Error creando juego:", error);
      setError("Error al crear el juego");
    } finally {
      setLoading(false);
    }
  };

  const updateGameScore = async (
    gameId: string,
    pair1Games: number,
    pair2Games: number
  ) => {
    try {
      setLoading(true);
      await updateGame(gameId, {
        pair1_games: pair1Games,
        pair2_games: pair2Games,
      });

      // Actualizar estado local
      setGames((prevGames) =>
        prevGames.map((game) =>
          game.id === gameId
            ? { ...game, pair1_games: pair1Games, pair2_games: pair2Games }
            : game
        )
      );

      console.log(`Marcador actualizado: ${pair1Games}-${pair2Games}`);
    } catch (error) {
      console.error("Error actualizando marcador:", error);
      setError("Error al actualizar el marcador");
    } finally {
      setLoading(false);
    }
  };

  const updateTieBreakScore = async (
    gameId: string,
    pair1Points: number,
    pair2Points: number
  ) => {
    try {
      setLoading(true);
      await updateGame(gameId, {
        tie_break_pair1_points: pair1Points,
        tie_break_pair2_points: pair2Points,
        is_tie_break: true,
      });

      // Actualizar estado local
      setGames((prevGames) =>
        prevGames.map((game) =>
          game.id === gameId
            ? {
                ...game,
                tie_break_pair1_points: pair1Points,
                tie_break_pair2_points: pair2Points,
                is_tie_break: true,
              }
            : game
        )
      );

      console.log(`Tie-break actualizado: ${pair1Points}-${pair2Points}`);
    } catch (error) {
      console.error("Error actualizando tie-break:", error);
      setError("Error al actualizar el tie-break");
    } finally {
      setLoading(false);
    }
  };

  const removeGame = async (gameId: string) => {
    try {
      setLoading(true);
      await deleteGame(gameId);
      setGames((prevGames) => prevGames.filter((game) => game.id !== gameId));
      console.log("Juego eliminado:", gameId);
    } catch (error) {
      console.error("Error eliminando juego:", error);
      setError("Error al eliminar el juego");
    } finally {
      setLoading(false);
    }
  };

  const recalculateMatchWinner = async () => {
    console.log("=== RECALCULANDO GANADOR DEL PARTIDO ===");
    try {
      setLoading(true);

      // Obtener todos los juegos del partido
      const matchGames = await getGames(match.id);
      console.log("Games for recalculation:", matchGames.length);

      if (matchGames.length === 0) {
        console.log("No hay juegos para recalcular");
        return;
      }

      // Calcular estad√≠sticas actuales
      let pair1Games = 0;
      let pair2Games = 0;

      matchGames.forEach((game) => {
        if (game.is_tie_break) {
          if (
            game.tie_break_pair1_points >= 10 &&
            game.tie_break_pair1_points - game.tie_break_pair2_points >= 2
          ) {
            pair1Games++;
          } else if (
            game.tie_break_pair2_points >= 10 &&
            game.tie_break_pair2_points - game.tie_break_pair1_points >= 2
          ) {
            pair2Games++;
          }
        } else {
          if (game.pair1_games > game.pair2_games) {
            pair1Games++;
          } else if (game.pair2_games > game.pair1_games) {
            pair2Games++;
          }
        }
      });

      // Detectar empate y ganador
      const isTie = pair1Games === pair2Games;
      const winnerId = isTie
        ? undefined
        : pair1Games > pair2Games
        ? match.pair1_id
        : match.pair2_id;

      console.log("Recalculated statistics:");
      console.log("Pair 1 games:", pair1Games);
      console.log("Pair 2 games:", pair2Games);
      console.log("Is tie:", isTie);
      console.log("Winner ID:", winnerId);

      // Actualizar el partido con el nuevo ganador
      await updateMatch(match.id, {
        winner_id: winnerId,
        is_finished: true,
      });

      console.log("‚úÖ Ganador recalculado y actualizado en la base de datos");
    } catch (error) {
      console.error("Error recalculating match winner:", error);
      setError("Error al recalcular ganador");
    } finally {
      setLoading(false);
    }
  };

  const recalculatePairStatistics = async () => {
    try {
      console.log("üîÑ Recalculando estad√≠sticas de parejas...");

      // Obtener todas las parejas del torneo
      const pairs = await getPairs(match.tournament_id);

      // Obtener todos los partidos del torneo
      const allMatches = await getMatches(match.tournament_id);

      // Para cada pareja, recalcular estad√≠sticas
      for (const pair of pairs) {
        let setsWon = 0;
        let gamesWon = 0;
        let points = 0;
        let matchesPlayed = 0;

        // Buscar partidos donde participa esta pareja
        const pairMatches = allMatches.filter(
          (m) => m.pair1_id === pair.id || m.pair2_id === pair.id
        );

        for (const pairMatch of pairMatches) {
          if (pairMatch.is_finished) {
            matchesPlayed++;

            // Obtener juegos del partido
            const matchGames = await getGames(pairMatch.id);

            let pairGamesWon = 0;
            let pairGamesLost = 0;

            for (const game of matchGames) {
              const isPair1 = pairMatch.pair1_id === pair.id;
              const pairScore = isPair1 ? game.pair1_games : game.pair2_games;
              const opponentScore = isPair1
                ? game.pair2_games
                : game.pair1_games;

              if (pairScore > opponentScore) {
                pairGamesWon++;
                gamesWon++;
              } else if (pairScore < opponentScore) {
                pairGamesLost++;
              }

              // Sumar puntos del juego
              points += pairScore;
            }

            // Determinar si gan√≥ el set
            if (pairGamesWon > pairGamesLost) {
              setsWon++;
            } else if (pairGamesWon === pairGamesLost && pairGamesWon > 0) {
              // Empate en el set (solo si hay juegos jugados)
              setsWon += 0.5;
            }
          }
        }

        // Actualizar estad√≠sticas de la pareja
        await updatePair(pair.id, {
          sets_won: setsWon,
          games_won: gamesWon,
          points: points,
          matches_played: matchesPlayed,
        });

        console.log(`‚úÖ Pareja ${pair.id} actualizada:`, {
          setsWon,
          gamesWon,
          points,
          matchesPlayed,
        });
      }

      console.log("‚úÖ Estad√≠sticas recalculadas exitosamente");
    } catch (error) {
      console.error("‚ùå Error recalculando estad√≠sticas:", error);
    }
  };

  const finishMatch = async () => {
    try {
      setLoading(true);

      // Validar que se puede finalizar el partido
      if (!FlexibleMatchFinisher.canFinishMatch(games)) {
        setError(
          "No se puede finalizar el partido. Verifica que todos los juegos tengan marcadores v√°lidos."
        );
        return;
      }

      // Obtener parejas para el c√°lculo
      const allPairs = await getPairs(match.tournament_id);

      // Usar el nuevo FlexibleMatchFinisher
      const result = await FlexibleMatchFinisher.finishMatch(
        match,
        games,
        allPairs,
        async () => {
          // Recalcular estad√≠sticas de todas las parejas
          await recalculatePairStatistics();
          onUpdate(); // Notificar al componente padre
        }
      );

      if (result.success) {
        console.log("‚úÖ Partido finalizado exitosamente:", result);
        alert(result.message);
        onClose(); // Cerrar el corrector
      } else {
        setError(result.message);
      }
    } catch (error) {
      console.error("Error finalizando partido:", error);
      setError("Error al finalizar el partido");
    } finally {
      setLoading(false);
    }
  };

  const handleGameScoreChange = (
    gameId: string,
    pair: "pair1" | "pair2",
    value: number
  ) => {
    setEditingGames((prev) => ({
      ...prev,
      [gameId]: {
        ...prev[gameId],
        [pair]: value,
      },
    }));
  };

  const handleGameScoreBlur = async (gameId: string) => {
    const editing = editingGames[gameId];
    if (editing) {
      await updateGameScore(gameId, editing.pair1, editing.pair2);
      setEditingGames((prev) => {
        const newState = { ...prev };
        delete newState[gameId];
        return newState;
      });

      // Recalcular estad√≠sticas despu√©s de cambiar un marcador
      await recalculatePairStatistics();
    }
  };

  const handleTieBreakScoreChange = (
    gameId: string,
    pair: "pair1" | "pair2",
    value: number
  ) => {
    setEditingTieBreaks((prev) => ({
      ...prev,
      [gameId]: {
        ...prev[gameId],
        [pair]: value,
      },
    }));
  };

  const handleTieBreakScoreBlur = async (gameId: string) => {
    const editing = editingTieBreaks[gameId];
    if (editing) {
      await updateTieBreakScore(gameId, editing.pair1, editing.pair2);
      setEditingTieBreaks((prev) => {
        const newState = { ...prev };
        delete newState[gameId];
        return newState;
      });

      // Recalcular estad√≠sticas despu√©s de cambiar tie-break
      await recalculatePairStatistics();
    }
  };

  const toggleTieBreak = async (gameId: string) => {
    try {
      setLoading(true);
      const game = games.find((g) => g.id === gameId);
      if (game) {
        // Simplemente alternar el estado local por ahora
        setGames((prevGames) =>
          prevGames.map((game) =>
            game.id === gameId
              ? { ...game, is_tie_break: !game.is_tie_break }
              : game
          )
        );
      }
    } catch (error) {
      console.error("Error alternando tie-break:", error);
      setError("Error al alternar tie-break");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="score-corrector-overlay">
      <div className="score-corrector-modal">
        <div className="score-corrector-header">
          <h3>üîß Corrector de Resultados</h3>
          <button onClick={onClose} className="close-btn">
            ‚úï
          </button>
        </div>

        <div className="score-corrector-content">
          <div className="match-info">
            <h4>
              üìä {match.pair1?.player1?.name} y {match.pair1?.player2?.name} vs{" "}
              {match.pair2?.player1?.name} y {match.pair2?.player2?.name}
            </h4>
            <p>
              üèüÔ∏è Cancha {match.court} - Ronda {match.round}
            </p>
          </div>

          {error && <div className="error-message">{error}</div>}

          <div className="games-section">
            <div className="games-header">
              <h5>üéæ Juegos</h5>
              <button
                onClick={addGame}
                disabled={loading}
                className="add-game-btn"
              >
                ‚ûï Agregar Juego
              </button>
            </div>

            {games.map((game) => (
              <div key={game.id} className="game-card">
                <div className="game-header">
                  <h6>Juego {game.game_number}</h6>
                  <div className="game-actions">
                    <button
                      onClick={() => toggleTieBreak(game.id)}
                      disabled={loading}
                      className="tie-break-btn"
                    >
                      {game.is_tie_break ? "üîó" : "üîì"} Tie-Break
                    </button>
                    <button
                      onClick={() => removeGame(game.id)}
                      disabled={loading}
                      className="remove-game-btn"
                    >
                      üóëÔ∏è Eliminar
                    </button>
                  </div>
                </div>

                <div className="game-scores">
                  <div className="score-input">
                    <label>Pareja 1:</label>
                    <input
                      type="number"
                      min="0"
                      max="20"
                      value={
                        editingGames[game.id]?.pair1 !== undefined
                          ? editingGames[game.id].pair1
                          : game.pair1_games
                      }
                      onChange={(e) =>
                        handleGameScoreChange(
                          game.id,
                          "pair1",
                          parseInt(e.target.value) || 0
                        )
                      }
                      onBlur={() => handleGameScoreBlur(game.id)}
                      placeholder="0"
                    />
                  </div>

                  <div className="score-input">
                    <label>Pareja 2:</label>
                    <input
                      type="number"
                      min="0"
                      max="20"
                      value={
                        editingGames[game.id]?.pair2 !== undefined
                          ? editingGames[game.id].pair2
                          : game.pair2_games
                      }
                      onChange={(e) =>
                        handleGameScoreChange(
                          game.id,
                          "pair2",
                          parseInt(e.target.value) || 0
                        )
                      }
                      onBlur={() => handleGameScoreBlur(game.id)}
                      placeholder="0"
                    />
                  </div>
                </div>

                {game.is_tie_break && (
                  <div className="tie-break-scores">
                    <h6>üîó Tie-Break</h6>
                    <div className="score-input">
                      <label>Pareja 1:</label>
                      <input
                        type="number"
                        min="0"
                        max="20"
                        value={
                          editingTieBreaks[game.id]?.pair1 !== undefined
                            ? editingTieBreaks[game.id].pair1
                            : game.tie_break_pair1_points
                        }
                        onChange={(e) =>
                          handleTieBreakScoreChange(
                            game.id,
                            "pair1",
                            parseInt(e.target.value) || 0
                          )
                        }
                        onBlur={() => handleTieBreakScoreBlur(game.id)}
                        placeholder="0"
                      />
                    </div>

                    <div className="score-input">
                      <label>Pareja 2:</label>
                      <input
                        type="number"
                        min="0"
                        max="20"
                        value={
                          editingTieBreaks[game.id]?.pair2 !== undefined
                            ? editingTieBreaks[game.id].pair2
                            : game.tie_break_pair2_points
                        }
                        onChange={(e) =>
                          handleTieBreakScoreChange(
                            game.id,
                            "pair2",
                            parseInt(e.target.value) || 0
                          )
                        }
                        onBlur={() => handleTieBreakScoreBlur(game.id)}
                        placeholder="0"
                      />
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>

          <div className="corrector-actions">
            <button
              onClick={recalculateMatchWinner}
              disabled={loading}
              className="recalculate-winner-btn"
            >
              {loading ? "‚è≥ Procesando..." : "üîÑ Recalcular Ganador"}
            </button>
            <button
              onClick={finishMatch}
              disabled={loading || games.length === 0}
              className="finish-match-btn"
            >
              {loading ? "‚è≥ Procesando..." : "üèÜ Finalizar Partido"}
            </button>
            <button onClick={onClose} className="cancel-btn">
              ‚ùå Cancelar
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ScoreCorrector;
