import React, { useState, useEffect, useMemo } from "react";
import { getPairs, getMatches, getGames, Pair } from "../lib/database";
import { SimpleMatchCalculator } from "./SimpleMatchCalculator";

interface PairWithPlayers {
  id: string;
  tournament_id: string;
  player1_id: string;
  player2_id: string;
  sets_won: number;
  games_won: number;
  points: number;
  matches_played: number;
  created_at: string;
  updated_at: string;
  player1?: {
    id: string;
    name: string;
  };
  player2?: {
    id: string;
    name: string;
  };
}

interface Match {
  id: string;
  tournament_id: string;
  pair1_id: string;
  pair2_id: string;
  court: number;
  round: number;
  winner_id?: string;
  is_finished: boolean;
  created_at: string;
  updated_at: string;
  pair1?: Pair;
  pair2?: Pair;
}

interface Game {
  id: string;
  match_id: string;
  game_number: number;
  pair1_games: number;
  pair2_games: number;
  is_tie_break: boolean;
  tie_break_pair1_points?: number;
  tie_break_pair2_points?: number;
  created_at: string;
  updated_at: string;
}

interface StandingsTableProps {
  tournamentId: string;
  forceRefresh: number;
}

const StandingsTable: React.FC<StandingsTableProps> = ({
  tournamentId,
  forceRefresh,
}) => {
  const [pairs, setPairs] = useState<PairWithPlayers[]>([]);
  const [matches, setMatches] = useState<Match[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // Cargar datos cuando cambie el tournamentId o forceRefresh
  useEffect(() => {
    if (tournamentId) {
      loadTournamentData();
    }
  }, [tournamentId, forceRefresh]);

  const loadTournamentData = async () => {
    try {
      setLoading(true);
      setError("");

      console.log("üîÑ Cargando datos para tabla de clasificaci√≥n...");

      // Cargar parejas y partidos en paralelo
      const [pairsData, matchesData] = await Promise.all([
        getPairs(tournamentId),
        getMatches(tournamentId),
      ]);

      setPairs(pairsData);
      setMatches(matchesData);

      console.log(
        `‚úÖ Datos cargados: ${pairsData.length} parejas, ${matchesData.length} partidos`
      );
    } catch (error) {
      console.error("‚ùå Error cargando datos:", error);
      setError("Error al cargar los datos del torneo");
    } finally {
      setLoading(false);
    }
  };

  // Recalcular todas las estad√≠sticas
  const recalculateAllStatistics = async () => {
    try {
      console.log("üîÑ Recalculando todas las estad√≠sticas...");
      setLoading(true);
      setError("");

      // Usar la nueva funci√≥n de rec√°lculo completo
      const result = await SimpleMatchCalculator.recalculateAllStatistics(
        tournamentId
      );

      if (result.success) {
        console.log("‚úÖ Estad√≠sticas recalculadas exitosamente");
        // Recargar datos despu√©s del rec√°lculo
        await loadTournamentData();
      } else {
        console.error("‚ùå Error en rec√°lculo:", result.message);
        setError(result.message);
      }
    } catch (error) {
      console.error("‚ùå Error recalculando estad√≠sticas:", error);
      setError("Error al recalcular estad√≠sticas");
    } finally {
      setLoading(false);
    }
  };

  // Ordenar parejas usando SETS GANADOS como criterio principal
  const sortedPairs = useMemo(() => {
    return [...pairs].sort((a, b) => {
      // Criterio 1: Sets ganados (descendente) - CRITERIO PRINCIPAL
      if (b.sets_won !== a.sets_won) {
        return b.sets_won - a.sets_won;
      }
      // Criterio 2: Puntos totales (descendente)
      if (b.points !== a.points) {
        return b.points - a.points;
      }
      // Criterio 3: Menos partidos jugados (mejor eficiencia)
      return a.matches_played - b.matches_played;
    });
  }, [pairs]);

  // Detectar empates en partidos
  const detectTies = () => {
    const ties: { matchId: string; pair1: string; pair2: string }[] = [];

    matches.forEach((match) => {
      if (match.is_finished && !match.winner_id) {
        ties.push({
          matchId: match.id,
          pair1: `${match.pair1?.player1?.name} y ${match.pair1?.player2?.name}`,
          pair2: `${match.pair2?.player1?.name} y ${match.pair2?.player2?.name}`,
        });
      }
    });

    return ties;
  };

  const ties = detectTies();

  if (loading) {
    return (
      <div className="standings">
        <h3>üìä Clasificaci√≥n</h3>
        <div className="loading">‚è≥ Cargando clasificaci√≥n...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="standings">
        <h3>üìä Clasificaci√≥n</h3>
        <div className="error">{error}</div>
      </div>
    );
  }

  return (
    <div className="standings">
      <div className="standings-header">
        <h3>üìä Clasificaci√≥n</h3>
        <button
          onClick={recalculateAllStatistics}
          className="recalculate-stats-btn"
          disabled={loading}
        >
          üîÑ Recalcular Estad√≠sticas
        </button>
      </div>

      {ties.length > 0 && (
        <div className="ties-notice">
          <h4>ü§ù Partidos con Empate</h4>
          <ul>
            {ties.map((tie) => (
              <li key={tie.matchId}>
                {tie.pair1} vs {tie.pair2} - Empate
              </li>
            ))}
          </ul>
        </div>
      )}

      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th>Pos</th>
              <th>Pareja</th>
              <th>Sets</th>
              <th>Partidos</th>
              <th>Puntos</th>
            </tr>
          </thead>
          <tbody>
            {sortedPairs.map((pair, index) => (
              <tr key={pair.id} className={`position-${index + 1}`}>
                <td className="position">
                  {index + 1}
                  {index === 0 && "ü•á"}
                  {index === 1 && "ü•à"}
                  {index === 2 && "ü•â"}
                </td>
                <td className="pair-name">
                  {pair.player1?.name} y {pair.player2?.name}
                </td>
                <td className="points">{pair.sets_won}</td>
                <td className="points">{pair.matches_played}</td>
                <td className="points">{pair.points}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {sortedPairs.length === 0 && (
        <div className="no-pairs">
          <p>üìù No hay parejas registradas en este torneo</p>
        </div>
      )}
    </div>
  );
};

export default StandingsTable;
